from collections import defaultdict

SERVICES = {
    'api': {'url': 'http://localhost:8000/health', 'restart_cmd': 'systemctl restart api'},
    'worker': {'url': 'http://localhost:8001/health', 'restart_cmd': 'systemctl restart worker'},
